mapper.select:
SELECT club_id, club_name, club_description, user_id
FROM Club
WHERE club_id = ${clubId};

SELECT club_id, club_name, club_description, user_id
FROM Club
WHERE club_name = #{clubName};

SELECT club_id, club_name, club_description, user_id
FROM Club
WHERE user_id = #{userId};

SELECT club_id, club_name, club_description, user_id
FROM Club
WHERE club_name LIKE CONCAT ('%', #{clubName}, '%');

mapper.delete:
DELETE
FROM Club
WHERE user_id = #{userId} AND club_name = #{clubName};
