mapper.select:
SELECT club_id, user_id, access
FROM Member
WHERE club_id = ${club_id};

SELECT club_id, user_id, access
FROM Member
WHERE club_id = ${clubId} AND user_id = #{userId};

mapper.update:
UPDATE Member
SET access = 1
WHERE club_id = ${clubId} AND user_id = #{userId};

mapper.delete:
DELETE
FROM Member
WHERE club_id = ${clubId} AND user_id = #{userId};
